sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.tickets=[];
	var gr=new GlideRecord('x_844144_movie_man_ticket');
	gr.addQuery('sys_created_by',gs.getUserName());
	gr.query();
	while(gr.next()){
		var ticket_data={};
		ticket_data.Ticket_no=gr.getValue('number');
		ticket_data.theatre=gr.getDisplayValue('theatre');
		ticket_data.no_of_tickets=gr.getValue('no_of_tickets');
		ticket_data.total_price=gr.getValue('total_price');
		data.tickets.push(ticket_data);
	}

})();
